 * @param {string|Array} scopes - Scopes as string or array
 * @returns {string} Formatted scope string
 */
function formatOAuthScopes(scopes) {
  if (Array.isArray(scopes)) {
    return scopes.join(' ');
  }
  return scopes || 'OAuth2ReadPII OAuth2Read OAuth2Write';
}

module.exports = {
  createAuth,
  formatOAuthScopes,
  AuthManager
};
