    const timestamp = new Date().toLocaleTimeString();
    entry.textContent = `[${timestamp}] Debugging auth state - Token ${_authToken ? 'present' : 'missing'}`;
    logOutput.appendChild(entry);
    logOutput.scrollTop = logOutput.scrollHeight;
  }

