let lastRequest = null;
let lastResponse = null;
let requestLog = [];

// Modify the apiCall function
export async function apiCall(endpoint, method = 'GET', body = null) {
  try {
    // Build the options for the fetch request
    const options = { 
      method, 
      headers: {
        'Content-Type': 'application/json'
      }
    };
    
