    lastRequest = {
      url: `/api/${endpoint}`,
      method,
      headers: {...options.headers},
      body: body ? JSON.stringify(body) : null,
      timestamp: new Date().toISOString()
    };
    
    console.log(`API Request: ${method} /api/${endpoint}`, {
      hasToken: !!token,
      tokenLength: token ? token.length : 0, 
      headers: options.headers
    });
    
    // Make the request
    const response = await fetch(`/api/${endpoint}`, options);
    
    // Log response details
    const responseData = await response.json();
    lastResponse = {
      status: response.status,
      statusText: response.statusText,
      headers: Object.fromEntries([...response.headers.entries()]),
      data: responseData,
      timestamp: new Date().toISOString()
    };
    
    // Add to request log (keep last 10)
    requestLog.unshift({request: lastRequest, response: lastResponse});
    if (requestLog.length > 10) requestLog.pop();
    
    console.log(`API Response (${response.status}):`, responseData);
    
    // Handle non-success responses
    if (!response.ok) {
      throw new Error(responseData.error || `API error: ${response.status}`);
    }
    
    return responseData;
  } catch (error) {
    console.error('API call error:', error);
    throw error;
  }
}

// Add a new function to get network logs
export function getNetworkLogs() {
  return {
    lastRequest,
    lastResponse,
    requestLog
  };
}

