// Import the functions that return auth information
import { getToken, getAuthMethod } from './clientAuth.js';

/**
 * Make an API call to the backend server
 */
export async function apiCall(endpoint, method = 'GET', body = null) {
  try {
    const startTime = performance.now();

    // Build the options for the fetch request
    const options = { 
      method, 
      headers: {
        'Content-Type': 'application/json'
      }
    };

    // Get the current auth method and token
    const authMethod = getAuthMethod();
    const token = getToken();

    // Add auth token if using OAuth
    if (token && token !== 'API_KEY_AUTH') {
      options.headers['Authorization'] = `Bearer ${token}`;
      logDebug(`Adding OAuth token to request (${token.substring(0, 5)}...)`);
    } 
    // For API key auth, no header needed - server handles it based on session
    else if (authMethod === 'apikey') {
      logDebug('Using API key authentication for request');
    }

    // Rest of the function remains the same...
  } catch (error) {
    // Error handling...
  }
}
