const express = require('express');
const router = express.Router();
const logger = require('../utils/logger');
const OnshapeClient = require('../services/onshapeClient');

// Create a scoped logger
const log = logger.scope('DocumentRoutes');

/**
 * @route GET /api/documents
 * @description Get all documents for the authenticated user
 */
router.get('/', async (req, res) => {
  log.info('Fetching documents');

  try {
    const authManager = req.app.get('authManager');
    if (!authManager) {
      log.error('Auth manager not found in app context');
      return res.status(500).json({ error: 'Auth manager not configured' });
    }

    log.info(`Using auth method: ${authManager.getMethod()}`);

    // For API key auth, check that we have keys
    if (authManager.getMethod() === 'apikey') {
      log.info('Using API key authentication for Onshape request');

      if (!authManager.accessKey || !authManager.secretKey) {
        log.error('API key authentication is configured but keys are missing');
        return res.status(401).json({ error: 'API key credentials not configured properly' });
      }

      log.info('API key authentication credentials are present');
    }
    // For OAuth, check that we have a token
    else if (!req.session.oauthToken && !req.user?.accessToken) {
      log.error('OAuth authentication but no token found in session or user object');
      return res.status(401).json({ error: 'Authentication token required' });
    }

    // Initialize Onshape client
    const onshapeClient = new OnshapeClient(authManager);

    // Fetch documents from Onshape API
    try {
      log.info('Making API call to Onshape to fetch documents');
      const documents = await onshapeClient.getDocuments();
      log.info(`Successfully fetched ${documents.items?.length || 0} documents`);

      return res.json(documents.items || documents);
    } catch (apiError) {
