// src/middleware/error.js
const logger = require('../utils/logger');
const { OnshapeError, AuthenticationError } = require('../utils/errors');

// Create a scoped logger
const log = logger.scope('ErrorMiddleware');

/**
 * Global error handling middleware
 * Handles different types of errors and returns appropriate responses
 */
function errorMiddleware(err, req, res, next) {
  // Extract useful information from the error
  let statusCode = err.statusCode || err.response?.status || 500;
  let message = err.message || 'Internal server error';
  let errorType = err.name || 'Error';

  // Create error details object
  const errorDetails = {
    message,
    status: statusCode,
    type: errorType,
    path: req.path,
    method: req.method
  };

  // Add more details for debugging in development mode
  if (process.env.NODE_ENV !== 'production') {
    errorDetails.stack = err.stack;

    // Add original error details if available
    if (err.originalError) {
      errorDetails.originalError = {
        message: err.originalError.message,
        name: err.originalError.name,
        stack: err.originalError.stack
      };
    }

    // Add response data if available
    if (err.response?.data) {
      errorDetails.responseData = err.response.data;
    }

    // Add request details that might help with debugging
    errorDetails.requestInfo = {
      url: req.originalUrl,
      query: req.query,
      headers: {
        accept: req.headers.accept,
        contentType: req.headers['content-type'],
        userAgent: req.headers['user-agent']
      }
    };
  }

  // Log the error with appropriate severity
  if (statusCode >= 500) {
    log.error(`Server Error (${statusCode}): ${message} at ${req.method} ${req.path}`, err);
    // Ensure stack trace is visible in console for severe errors
    console.error('Stack trace:', err.stack);

    // Log original error details if available
    if (err.originalError) {
      console.error('Original error:', err.originalError);
    }
  } else if (statusCode === 401 || statusCode === 403) {
    log.warn(`Auth Error (${statusCode}): ${message} at ${req.method} ${req.path}`, {
      path: req.path,
      hasSession: !!req.session,
      isAuthenticated: !!req.isAuthenticated
    });
  } else {
    log.warn(`Client Error (${statusCode}): ${message} at ${req.method} ${req.path}`);
  }

  // Handle API-specific errors
  if (err.response?.data && err.response.data.message) {
    // Extract API-specific error messages
    message = `API Error: ${err.response.data.message}`;

    // Add more context if available
    if (err.response.data.code) {
      message += ` (Code: ${err.response.data.code})`;
    }

    // Add to error details
    errorDetails.apiError = err.response.data;
  }

  // Handle authentication errors
  if (statusCode === 401 || err instanceof AuthenticationError) {
    // Clear any invalid tokens from session
    if (req.session) {
      delete req.session.oauthToken;
      delete req.session.refreshToken;
    }

    // For API requests, return JSON error
    if (req.xhr || req.path.startsWith('/api/')) {
      return res.status(401).json({ 
        error: 'Authentication failed',
        message: 'Your session has expired or is invalid. Please log in again.',
        details: process.env.NODE_ENV !== 'production' ? {
          authError: message,
          suggestedAction: 'Check API key format or refresh OAuth token'
        } : undefined
      });
    }

    // For browser requests, redirect to login
    return res.redirect('/oauth/login');
  }

  // Handle not found errors
  if (statusCode === 404) {
    if (req.xhr || req.path.startsWith('/api/')) {
      return res.status(404).json({
        error: 'Not found',
        message: message
      });
    }

    // For HTML requests, render a nice 404 page (if we had a view engine)
    // For now, just return a simple 404 message
    return res.status(404).send('Not Found: ' + message);
  }

  // Handle validation errors
  if (statusCode === 400) {
    return res.status(400).json({
      error: 'Validation error',
      message: message,
      details: process.env.NODE_ENV !== 'production' ? errorDetails : undefined
    });
  }

  // Handle other errors
  res.status(statusCode).json({
    error: statusCode >= 500 ? 'Server error' : 'Request error',
    message,
    details: process.env.NODE_ENV !== 'production' ? errorDetails : undefined
  });
}

module.exports = errorMiddleware;
```

This enhancement to the error middleware provides several benefits:

1. **More Detailed Error Information**:
   - Includes the full stack trace of both the main error and any original/underlying errors
   - Adds request details (URL, query parameters, relevant headers) to help with debugging
   - Extracts and formats API-specific error messages when available

2. **Improved Console Logging**:
   - Ensures stack traces are always visible in the console for severe server errors
   - Adds the request method and path to all error logs for better context
   - Logs original error details separately for clearer debugging

3. **Better Authentication Error Handling**:
   - Adds more specific information for authentication errors
   - Includes suggestions for resolving auth issues in development mode

These improvements will make it significantly easier to diagnose API authentication issues and other errors while maintaining all the existing functionality of the error middleware.

