const DocumentsApi = require('../api/endpoints/documents');
const config = require('../../config');

// Create a scoped logger
const log = logger.scope('DocumentRoutes');

// Middleware to create Onshape client from request
router.use((req, res, next) => {
  req.onshapeClient = createClientFromRequest(req, OnshapeClient);
  if (!req.onshapeClient) {
    return res.status(500).json({ error: 'Failed to initialize Onshape client' });
  }
  next();
});

