        return res.json([]);
      }

      // Normalize the response
      const documents = Array.isArray(documentsResponse) 
        ? documentsResponse 
        : (documentsResponse.items || []);

      return res.json(documents);

    } catch (error) {
      log.error(`Error fetching documents: ${error.message}`);
      next(error);
    }
  });

  // ...existing route handlers...

  return router;
};
```

