// Initialize authentication middleware once
const authMiddleware = require('./src/middleware/authMiddleware')(app);

// Mount routes with auth middleware
app.use('/api/auth', require('./src/routes/apiAuthRoutes')(app, authMiddleware));
app.use('/api/documents', require('./src/routes/documents')(app, authMiddleware));
app.use('/api/elements', require('./src/routes/elements')(app, authMiddleware));
app.use('/api/features', require('./src/routes/features')(app, authMiddleware));
