const express = require('express');
const router = express.Router();
const logger = require('../utils/logger');

const log = logger.scope('ApiAuthRoutes');

module.exports = function(app, auth) {
  const { isAuthenticated } = auth;

  /**
   * @route GET /api/auth/status
   * @description Get authentication status
   * @access Public
   */
  router.get('/status', async (req, res) => {
    try {
      const authManager = req.app.get('authManager');
      const isAuth = req.isAuthenticated() || (req.user && req.user.method === 'apikey');

      res.json({
        isAuthenticated: isAuth,
        method: authManager ? authManager.getMethod() : null,
        session: req.session ? {
          hasSession: !!req.session,
          hasOAuthToken: !!req.session.oauthToken,
          hasRefreshToken: !!req.session.refreshToken
        } : null,
        user: req.user || null
      });
    } catch (error) {
      log.error(`Error checking auth status: ${error.message}`);
      res.status(500).json({ error: 'Internal server error' });
    }
  });

  /**
   * @route GET /api/auth/method
   * @description Get current authentication method
   * @access Public
   */
  router.get('/method', (req, res) => {
    try {
      const authManager = req.app.get('authManager');
      res.json({
        method: authManager ? authManager.getMethod() : null,
        isConfigured: !!authManager
      });
    } catch (error) {
      log.error(`Error getting auth method: ${error.message}`);
      res.status(500).json({ error: 'Internal server error' });
    }
  });

  /**
   * @route GET /api/auth/test
   * @description Test authentication
   * @access Private
   */
  router.get('/test', isAuthenticated, (req, res) => {
    try {
      const authManager = req.app.get('authManager');
      res.json({
        success: true,
        authenticated: true,
        method: authManager ? authManager.getMethod() : null,
        user: req.user || null
      });
    } catch (error) {
      log.error(`Error testing auth: ${error.message}`);
      res.status(500).json({ error: 'Internal server error' });
    }
  });

  return router;
};
```

