// load-env.js
const dotenv = require('dotenv');
const fs = require('fs');
const path = require('path');

// Check for .env file
const envPath = path.resolve(process.cwd(), '.env');
if (fs.existsSync(envPath)) {
  console.log('Loading environment variables from .env file');
  dotenv.config({ path: envPath });
} else {
  console.warn('\x1b[33m%s\x1b[0m', 'Warning: .env file not found at: ' + envPath);
  console.warn('\x1b[33m%s\x1b[0m', 'Create an .env file for proper configuration or set environment variables.');
  
  // Load from .env.example if it exists (for development)
  const examplePath = path.resolve(process.cwd(), '.env.example');
  if (process.env.NODE_ENV !== 'production' && fs.existsSync(examplePath)) {
    console.log('Loading environment variables from .env.example (development only)');
    dotenv.config({ path: examplePath });
  }
}

// Check required environment variables
const checkRequiredEnvs = () => {
  const requiredList = [];
  
  // Add auth-specific requirements
  const authMethod = (process.env.ONSHAPE_AUTH_METHOD || 'oauth').toLowerCase();
  if (authMethod === 'oauth') {
    requiredList.push('OAUTH_CLIENT_ID', 'OAUTH_CLIENT_SECRET', 'OAUTH_CALLBACK_URL');
  } else if (authMethod === 'apikey' || authMethod === 'api_key') {
    requiredList.push('ONSHAPE_ACCESS_KEY', 'ONSHAPE_SECRET_KEY');
  }
  
  // Check for missing values
  const missing = requiredList.filter(key => !process.env[key]);
  
  if (missing.length > 0) {
    console.warn('\x1b[33m%s\x1b[0m', `Missing required environment variables: ${missing.join(', ')}`);
    if (process.env.NODE_ENV === 'production') {
      throw new Error('Missing required environment variables in production mode');
    }
    return false;
  }
  
  return true;
};

// Export check function
module.exports = {
  checkRequiredEnvs
};

