const dotenv = require('dotenv');
const fs = require('fs');
const path = require('path');
const logger = require('./logger');

// Create scoped logger
const log = logger.scope('EnvLoader');

/**
 * Load environment variables from .env file
 */
function loadEnv() {
  const envPath = path.resolve(process.cwd(), '.env');
  if (fs.existsSync(envPath)) {
    log.info('Loading environment variables from .env file');
    dotenv.config({ path: envPath });
  } else {
    log.warn('Warning: .env file not found at:', envPath);
    log.warn('Create an .env file for proper configuration or set environment variables.');

    // Load from .env.example if it exists (for development)
    const examplePath = path.resolve(process.cwd(), '.env.example');
    if (process.env.NODE_ENV !== 'production' && fs.existsSync(examplePath)) {
      log.info('Loading environment variables from .env.example (development only)');
      dotenv.config({ path: examplePath });
    }
  }
}

/**
 * Check required environment variables
 */
function checkRequiredEnvs() {
  const requiredList = [];

  // Add auth-specific requirements
  const authMethod = (process.env.ONSHAPE_AUTH_METHOD || 'oauth').toLowerCase();
  if (authMethod === 'oauth') {
