const dotenv = require('dotenv');
const path = require('path');
const fs = require('fs');
const logger = require('./logger');

// Create scoped logger
const log = logger.scope('EnvLoader');

/**
 * Validate environment variables based on auth method
 */
function validateEnv() {
  const authMethod = (process.env.ONSHAPE_AUTH_METHOD || 'oauth').toLowerCase();
  const messages = [];
  let isValid = true;

  // Common required variables
  const commonVars = ['ONSHAPE_BASE_URL', 'SESSION_SECRET'];

  // Auth-specific required variables
  const authVars = authMethod === 'oauth' 
    ? ['OAUTH_CLIENT_ID', 'OAUTH_CLIENT_SECRET', 'OAUTH_CALLBACK_URL']
    : ['ONSHAPE_ACCESS_KEY', 'ONSHAPE_SECRET_KEY'];

  // Check common variables
  commonVars.forEach(varName => {
    if (!process.env[varName]) {
      messages.push(`Missing required variable: ${varName}`);
      isValid = false;
    }
  });

  // Check auth-specific variables
  authVars.forEach(varName => {
    if (!process.env[varName]) {
      messages.push(`Missing ${authMethod} auth variable: ${varName}`);
      isValid = false;
    }
  });

  return { isValid, messages };
}

/**
 * Load environment variables from file
 */
function loadEnv() {
  const envPath = path.resolve(process.cwd(), '.env');

  if (fs.existsSync(envPath)) {
    log.info(`Loading environment from: ${envPath}`);
    const result = dotenv.config({ path: envPath });

    if (result.error) {
      log.error('Error loading .env file:', result.error);
      return false;
    }

    log.info('Environment variables loaded successfully');

    // Validate loaded environment
    const { isValid, messages } = validateEnv();

    if (!isValid) {
      messages.forEach(msg => log.warn(msg));

      if (process.env.NODE_ENV === 'production') {
        throw new Error('Missing required environment variables in production mode');
      }

      // Try loading from example file in development
      if (process.env.NODE_ENV !== 'production') {
        const examplePath = path.resolve(process.cwd(), '.env.example');
        if (fs.existsSync(examplePath)) {
          log.info('Loading development values from .env.example');
          dotenv.config({ path: examplePath });
        }
      }
    }

    // Log auth method configuration
    const authMethod = process.env.ONSHAPE_AUTH_METHOD || 'oauth';
    log.info(`Using ${authMethod} authentication method`);

    if (authMethod === 'apikey' || authMethod === 'api_key') {
      if (process.env.ONSHAPE_ACCESS_KEY && process.env.ONSHAPE_SECRET_KEY) {
        log.info('API key credentials configured');
      } else {
        log.warn('API key authentication selected, but credentials missing');
      }
    } else {
      const hasOAuthCreds = (process.env.OAUTH_CLIENT_ID && process.env.OAUTH_CLIENT_SECRET) || 
                           (process.env.ONSHAPE_CLIENT_ID && process.env.ONSHAPE_CLIENT_SECRET);
      if (hasOAuthCreds) {
        log.info('OAuth credentials configured');
      } else {
        log.warn('OAuth credentials missing');
      }
    }

    return true;
  } else {
    log.warn('.env file not found at:', envPath);
    return false;
  }
}

// Initialize environment on module load
const initialized = loadEnv();

// Export utility functions
module.exports = {
  loadEnv,
  validateEnv,
  initialized
};
