    log.error('- ONSHAPE_AUTH_METHOD=apikey');
    log.error('- ONSHAPE_ACCESS_KEY');
    log.error('- ONSHAPE_SECRET_KEY');
    throw new Error('Missing .env file');
  }

  const result = dotenv.config({ path: envPath });

  if (result.error) {
    log.error('Error loading .env file:', result.error);
    throw result.error;
  }

  // Validate environment
  const { isValid, messages } = validateEnv();

  if (!isValid) {
    messages.forEach(msg => log.error(msg));
    throw new Error('Invalid environment configuration');
  }

  // Log successful configuration
  const authMethod = process.env.ONSHAPE_AUTH_METHOD || 'oauth';
  log.info(`Environment loaded successfully using ${authMethod} authentication`);

  return true;
}

// Initialize environment
let initialized = false;
try {
  initialized = loadEnv();
} catch (error) {
  log.error('Failed to initialize environment:', error.message);
  process.exit(1);
}

