/**
 * Helper function to create Onshape client from request
 */
const createClientFromRequest = (req) => {
    const authManager = req.app.get('authManager');
    const config = req.app.get('config');

    if (!authManager) {
        log.error('No auth manager found in app context');
        return null;
    }

    if (!config || !config.onshape) {
        log.error('Invalid configuration: missing onshape config');
        return null;
    }

    try {
        // For OAuth, set the token from session if available
        if (authManager.getMethod() === 'oauth' && req.session && req.session.oauthToken) {
            authManager.accessToken = req.session.oauthToken;
            authManager.refreshToken = req.session.refreshToken || null;
        }

        const clientOptions = {
            baseUrl: config.onshape.baseUrl || process.env.ONSHAPE_BASE_URL,
            authManager: authManager
        };

        const client = new OnshapeClient(clientOptions);
        log.debug(`Created Onshape client with ${authManager.getMethod()} authentication`);
        return client;
    } catch (error) {
        log.error(`Failed to create Onshape client: ${error.message}`);
        return null;
    }
};

/**
 * Authentication check middleware
 */
const isAuthenticated = (req, res, next) => {
    // Get the auth manager to check authentication method
    const authManager = req.app.get('authManager');
    const authMethod = authManager ? authManager.getMethod() : null;

    // Consider authenticated if:
    // 1. Using Passport session authentication (req.isAuthenticated())
    // 2. Using API key authentication (authMethod === 'apikey')
    // 3. OAuth token exists in session
    if (req.isAuthenticated() || 
        authMethod === 'apikey' || 
        (req.session && req.session.oauthToken)) {

        // Create client and attach to request
        if (!req.onshapeClient) {
            const client = createClientFromRequest(req);
            if (!client) {
                return res.status(500).json({ 
                    error: 'Server Error', 
                    message: 'Failed to initialize Onshape client' 
                });
            }
            req.onshapeClient = client;
        }

        log.debug(`User authenticated via ${authMethod || 'session'}`);
        return next();
    }

    // For API requests, return a 401 Unauthorized response
    if (req.xhr || req.path.startsWith('/api/')) {
        return res.status(401).json({ 
            error: 'Unauthorized', 
            message: 'Authentication required' 
        });
    }

    // For non-API requests, redirect to the login page
    res.redirect('/oauth/login');
};

// ...rest of the existing code...
