const fs = require('fs');
const path = require('path');
const env = require('./load-env');

/**
 * Custom logger that provides scoped logging capabilities
 */
class Logger {
  constructor(options = {}) {
    this.buffer = [];
    this.logLevels = {
      off: -1,     // New level to completely disable logging
      debug: 0,
      info: 1,
      warn: 2,
      error: 3
    };

    // Set global minimum log level (can be 'off', 'debug', 'info', 'warn', 'error')
    const globalLevel = options.logLevel || process.env.LOG_LEVEL || 'info';
    this.globalLogLevel = this.logLevels[globalLevel.toLowerCase()] !== undefined ? 
                          globalLevel.toLowerCase() : 'info';

    this.logToFile = options.logToFile || process.env.LOG_TO_FILE === 'true';
    this.logDir = options.logDir || 'logs';

    // Configure scope overrides
    this.scopeOverrides = options.scopeOverrides || {};

    // Ensure log directory exists if logging to file
    if (this.logToFile && !fs.existsSync(this.logDir)) {
      fs.mkdirSync(this.logDir, { recursive: true });
    }

    // Use buffered logs in test environment
    this.useBufferedLogs = process.env.NODE_ENV === 'test';
  }

  /**
   * Check if the message should be logged based on level and scope
   */
  shouldLog(level, message = '') {
    // Extract scope from message if it exists
    const scopeMatch = message.match(/\[(.*?)\]/);
    const scope = scopeMatch ? scopeMatch[1] : null;

    // Get the effective log level for this scope
    const effectiveLogLevel = this.getEffectiveLogLevel(scope);

    // If logging is turned off for this scope
    if (effectiveLogLevel === 'off') {
      return false;
    }

    // Check if the message's level meets the minimum threshold
    return this.logLevels[level] >= this.logLevels[effectiveLogLevel];
  }

  /**
   * Get the effective log level for a scope, considering overrides
   * @param {string} scope - The scope to check
   * @returns {string} - The effective log level
   */
  getEffectiveLogLevel(scope) {
    if (!scope) {
      return this.globalLogLevel;
    }

    // Check environment for scope-specific override
    const envOverride = env.getScopeLogLevel(scope);
    if (envOverride && this.logLevels[envOverride.toLowerCase()] !== undefined) {
      return envOverride.toLowerCase();
    }

    // Check programmatic scope overrides
    if (this.scopeOverrides[scope] && 
        this.logLevels[this.scopeOverrides[scope].toLowerCase()] !== undefined) {
      return this.scopeOverrides[scope].toLowerCase();
    }

    // Default to global level
    return this.globalLogLevel;
  }

  /**
   * Set log level override for a specific scope
   * @param {string} scope - The scope to configure
   * @param {string} level - The log level ('off', 'debug', 'info', 'warn', 'error')
   */
  setScopeLogLevel(scope, level) {
    if (this.logLevels[level.toLowerCase()] !== undefined) {
      this.scopeOverrides[scope] = level.toLowerCase();
      return true;
    }
    return false;
  }

  /**
   * Get current log level for a scope
   * @param {string} scope - The scope to check
   * @returns {string} - The current log level
   */
  getScopeLogLevel(scope) {
    return this.getEffectiveLogLevel(scope);
  }

  /**
   * Set the global minimum log level
   * @param {string} level - The log level ('off', 'debug', 'info', 'warn', 'error')
   */
  setGlobalLogLevel(level) {
    if (this.logLevels[level.toLowerCase()] !== undefined) {
      this.globalLogLevel = level.toLowerCase();
      return true;
    }
    return false;
  }

  // ... existing methods for info, debug, error, warn remain unchanged ...

  /**
   * Create a scoped logger that prefixes all messages with a scope name
   */
  scope(scopeName) {
    const scopedLogger = {};
    const methods = ['error', 'warn', 'info', 'debug'];

    methods.forEach(method => {
        scopedLogger[method] = (message, meta = {}) => {
            // Format the message with scope prefix
            // Don't add prefix if message already has one
            const scopedMessage = message.startsWith(`[${scopeName}]`) 
                ? message 
                : `[${scopeName}] ${message}`;

            // Call the original logger method (this refers to the Logger instance)
            return this[method](scopedMessage, meta instanceof Object ? meta : {});
        };
    });

    // Add auth method with same level checking as info
    scopedLogger.auth = (message, meta = {}) => {
        const scopedMessage = message.startsWith(`[${scopeName}]`)
            ? message 
            : `[${scopeName}] ${message}`;

        return this.info(scopedMessage, meta instanceof Object ? meta : {});
    };

    return scopedLogger;
  }

  // ... existing flush and other methods remain unchanged ...
}

// Export a singleton instance
const logger = new Logger();

// Set the initialized logger back in the env module
env.setLogger(logger.scope('Environment'));

// Export the logger functions
module.exports = logger;
