# Implementing Granular Log Level Control

I'll update the environment utility to support granular log levels for different scopes.

## Updates to c:\Dev\GitHub-Projects\onjs\src\utils\load-env.js

```javascript
// filepath: c:\Dev\GitHub-Projects\onjs\src\utils\load-env.js
const dotenv = require('dotenv');
const path = require('path');
const fs = require('fs');

// Define valid log levels and their hierarchy
const LOG_LEVELS = {
    'off': -1,
    'debug': 0,
    'info': 1,
    'warn': 2,
    'error': 3
};

// Store logging configuration
const loggingConfig = {
    GLOBAL: {
        enabled: true,
        level: 'info' // Default global log level
    }
};

// Use simple console logger initially to avoid circular dependency
const initialLog = {
    error: (msg, data) => console.error(msg, data || ''),
    warn: (msg, data) => console.warn(msg, data || ''),
    info: (msg, data) => console.info(msg, data || ''),
    debug: (msg, data) => console.debug(msg, data || '')
};

// This will be set to the real logger later
let log = initialLog;

/**
 * Get the log level for a specific scope
 * @param {string} scope - The scope to check
 * @returns {string} - The log level for this scope
 */
function getScopeLogLevel(scope) {
    // If scope-specific setting exists, use it
    if (loggingConfig[scope]) {
        return loggingConfig[scope].level;
    }

    // Fall back to global setting
    return loggingConfig.GLOBAL.level;
}

/**
 * Check if logging is enabled for a specific scope
 * @param {string} scope - The logging scope to check
 * @returns {boolean} - Whether logging is enabled for the scope
 */
function isLoggingEnabled(scope) {
    // If scope has 'off' level, it's disabled
    if (getScopeLogLevel(scope) === 'off') {
        return false;
    }

    // If scope-specific enabled flag exists, use it
    if (loggingConfig[scope]) {
        return loggingConfig[scope].enabled;
    }

    // Fall back to global setting
    return loggingConfig.GLOBAL.enabled;
}

/**
 * Check if the specified log level should be logged for a scope
 * @param {string} scope - The scope to check
 * @param {string} level - The log level to check
 * @returns {boolean} - Whether this level should be logged
 */
function shouldLogLevel(scope, level) {
    // If logging is not enabled for the scope, nothing should be logged
    if (!isLoggingEnabled(scope)) {
        return false;
    }

    // Get the minimum log level for this scope
    const scopeMinLevel = getScopeLogLevel(scope);

    // If the level is not recognized, default to allowing it
    if (!LOG_LEVELS.hasOwnProperty(level)) {
        return true;
    }

    // Check if the message level is at or above the scope's minimum level
    return LOG_LEVELS[level] >= LOG_LEVELS[scopeMinLevel];
}

/**
 * Extract logging configuration from environment variables
 * Looks for variables with patterns:
 * - SCOPE_LOGGING=true/false (backward compatibility)
 * - SCOPE_LOG_LEVEL=debug/info/warn/error/off
 */
function extractLoggingConfig() {
    // Process global settings first
    processGlobalLogSettings();

    // Track counts for summary
    let scopeCount = 0;
    const levelCounts = {};

    // Iterate through all environment variables for scope-specific settings
    Object.keys(process.env).forEach(key => {
        // Handle SCOPE_LOGGING=true/false
        if (key.endsWith('_LOGGING')) {
            const scope = key.slice(0, -8); // Remove '_LOGGING'

            // Skip global as it's handled separately
            if (scope === 'GLOBAL') return;

            // Initialize the scope config if needed
            if (!loggingConfig[scope]) {
                loggingConfig[scope] = {
                    enabled: true,
                    level: loggingConfig.GLOBAL.level // Inherit global level
                };
            }

            // Parse boolean value 
            const value = process.env[key];
            loggingConfig[scope].enabled = !(['false', '0', 'no', 'off'].includes(String(value).toLowerCase()));

            scopeCount++;
        }

        // Handle SCOPE_LOG_LEVEL=debug/info/warn/error/off
        if (key.endsWith('_LOG_LEVEL')) {
            const scope = key.slice(0, -10); // Remove '_LOG_LEVEL'

            // Initialize the scope config if needed
            if (!loggingConfig[scope]) {
                loggingConfig[scope] = {
                    enabled: true, // Default to enabled
                    level: loggingConfig.GLOBAL.level // Inherit global level by default
                };
            }

            const level = process.env[key].toLowerCase();

            // Only set if it's a valid level
            if (LOG_LEVELS.hasOwnProperty(level)) {
                loggingConfig[scope].level = level;
                levelCounts[level] = (levelCounts[level] || 0) + 1;
            }

            scopeCount++;
        }
    });

    // Log summary
    if (scopeCount > 0) {
        initialLog.debug(`Logging configuration initialized with ${scopeCount} scopes`);
        const levelSummary = Object.entries(levelCounts)
            .map(([level, count]) => `${level}=${count}`)
            .join(', ');

        if (levelSummary) {
            initialLog.debug(`Log levels: ${levelSummary}`);
        }
    }

    return loggingConfig;
}

/**
 * Process global logging settings from environment variables
 */
function processGlobalLogSettings() {
    // Process GLOBAL_LOGGING (enabled/disabled)
    const globalEnabled = process.env.GLOBAL_LOGGING !== 'false';

    // Process LOG_LEVEL (global minimum level)
    let globalLevel = (process.env.LOG_LEVEL || 'info').toLowerCase();

    // Validate the level
    if (!LOG_LEVELS.hasOwnProperty(globalLevel)) {
        globalLevel = 'info'; // Default to info if invalid
    }

    // Special case: if GLOBAL_LOGGING specifies a level instead of boolean
    if (LOG_LEVELS.hasOwnProperty(process.env.GLOBAL_LOGGING?.toLowerCase())) {
        globalLevel = process.env.GLOBAL_LOGGING.toLowerCase();
    }

    // Update global config
    loggingConfig.GLOBAL = {
        enabled: globalEnabled,
        level: globalLevel
    };
}

/**
 * Get all logging configuration
 * @returns {Object} - The current logging configuration
 */
function getLoggingConfig() {
    return JSON.parse(JSON.stringify(loggingConfig)); // Deep clone
}

/**
 * Show configuration example
 */
function showConfigExample() {
    log.info('Example .env configuration:');
    log.info('------------------------');
    log.info('# Authentication Method (oauth or apikey)');
    log.info('ONSHAPE_AUTH_METHOD=apikey');
    log.info('');
    log.info('# API Key Authentication');
    log.info('ONSHAPE_ACCESS_KEY=your_access_key');
    log.info('ONSHAPE_SECRET_KEY=your_secret_key');
    log.info('');
    log.info('# OAuth Authentication');
    log.info('OAUTH_CLIENT_ID=your_client_id');
    log.info('OAUTH_CLIENT_SECRET=your_client_secret');
    log.info('OAUTH_CALLBACK_URL=http://localhost:3000/oauth/callback');
    log.info('');
    log.info('# Common Configuration');
    log.info('ONSHAPE_BASE_URL=https://cad.onshape.com');
    log.info('SESSION_SECRET=your_session_secret');
    log.info('');
    log.info('# Logging Configuration');
    log.info('# Global log level. Options: off, debug, info, warn, error');
    log.info('LOG_LEVEL=info');
    log.info('GLOBAL_LOGGING=true');
    log.info('');
    log.info('# Scope-specific settings (override global)');
    log.info('Server_LOG_LEVEL=warn');
    log.info('Auth_LOG_LEVEL=error');
    log.info('ApiService_LOG_LEVEL=debug');
    log.info('Database_LOG_LEVEL=off');
    log.info('------------------------');
}

/**
 * Load and validate environment configuration
 */
function loadEnv() {
    const envPath = path.resolve(process.cwd(), '.env');

    if (!fs.existsSync(envPath)) {
        log.error(`No .env file found at: ${envPath}`);
        log.error('Please create a .env file with your configuration.');
        showConfigExample();
        throw new Error('Missing .env file');
    }

    const result = dotenv.config({ path: envPath });

    if (result.error) {
        log.error('Error loading .env file:', result.error);
        throw result.error;
    }

    log.info('Environment variables loaded successfully');

    // Initialize logging configuration
    extractLoggingConfig();
    log.info(`Logging configuration initialized with ${Object.keys(loggingConfig).length} scopes`);

    // Validate configuration
    const { isValid, errors } = validateEnv();

    if (!isValid) {
        errors.forEach(error => log.error(error));
        showConfigExample();
        throw new Error('Invalid environment configuration');
    }

    const authMethod = process.env.ONSHAPE_AUTH_METHOD || 'oauth';
    log.info(`Using ${authMethod} authentication`);

    return true;
}

/**
 * Validate environment configuration
 */
function validateEnv() {
    const authMethod = (process.env.ONSHAPE_AUTH_METHOD || 'oauth').toLowerCase();
    const errors = [];

    // Common required variables
    const commonVars = [
        'ONSHAPE_BASE_URL',
        'SESSION_SECRET'
    ];

    // Auth-specific required variables
    const authVars = authMethod === 'apikey' 
        ? ['ONSHAPE_ACCESS_KEY', 'ONSHAPE_SECRET_KEY']
        : ['OAUTH_CLIENT_ID', 'OAUTH_CLIENT_SECRET', 'OAUTH_CALLBACK_URL'];

    // Check common variables
    commonVars.forEach(varName => {
        if (!process.env[varName]) {
            errors.push(`Missing required variable: ${varName}`);
        }
    });

    // Check auth-specific variables
    authVars.forEach(varName => {
        if (!process.env[varName]) {
            errors.push(`Missing ${authMethod} auth variable: ${varName}`);
        }
    });

    return {
        isValid: errors.length === 0,
        errors
    };
}

/**
 * Set the logger instance (called from logger.js after it's initialized)
 */
function setLogger(logger) {
    log = logger;
}

// Initialize environment
let initialized = false;
try {
    initialized = loadEnv();
} catch (error) {
    initialLog.error('Failed to initialize environment:', error.message);
    process.exit(1);
}

module.exports = {
    loadEnv,
    validateEnv,
    initialized,
    isLoggingEnabled,
    getScopeLogLevel,
    shouldLogLevel,
    getLoggingConfig,
    setLogger,
    LOG_LEVELS
};
