/**
 * Client-side logging utility that can send logs to the server
 */
class ClientLogger {
  constructor(options = {}) {
    this.remoteLogging = options.remoteLogging !== false;
    this.remoteLogLevel = options.remoteLogLevel || 'warn'; // Only send warn+ to server by default
    this.consoleOutput = options.consoleOutput !== false;

    // Log levels with the same values as server-side
    this.logLevels = {
      off: -1,
      debug: 0,
      info: 1,
      warn: 2,
      error: 3
    };

    // Global minimum log level
    this.globalLogLevel = options.logLevel || 'info';

    // Scope-specific log levels
    this.scopeLogLevels = options.scopeLogLevels || {};

    // Initialize from window.LOG_CONFIG if available (set by server)
    this.initFromServerConfig();
  }

  /**
   * Initialize logging configuration from server-provided settings
   */
  initFromServerConfig() {
    if (window.LOG_CONFIG) {
      // Set global level
      if (window.LOG_CONFIG.globalLevel) {
        this.globalLogLevel = window.LOG_CONFIG.globalLevel;
      }

      // Set scope levels
      if (window.LOG_CONFIG.scopeLevels) {
        this.scopeLogLevels = window.LOG_CONFIG.scopeLevels;
      }

      console.debug(`ClientLogger initialized with global level: ${this.globalLogLevel}`);
    }
  }

  /**
   * Set log level for a specific scope
   * @param {string} scope - The scope to configure
   * @param {string} level - Log level (off, debug, info, warn, error)
   */
  setLogLevel(scope, level) {
    if (!this.logLevels.hasOwnProperty(level)) {
      console.error(`Invalid log level: ${level}`);
      return;
    }

    this.scopeLogLevels[scope] = level;
  }

  /**
   * Get log level for a specific scope
   * @param {string} scope - The scope to check
   * @returns {string} - The log level for this scope
   */
  getLogLevel(scope) {
    return this.scopeLogLevels[scope] || this.globalLogLevel;
  }

  /**
   * Determine if a message should be logged based on level and scope
   * @param {string} level - The log level
   * @param {string} message - The message to check
   * @returns {boolean} - Whether the message should be logged
   */
  shouldLog(level, message) {
    // Extract scope from message if it exists
    const scopeMatch = message.match(/\[(.*?)\]/);
    const scope = scopeMatch ? scopeMatch[1] : null;

    // Get the effective log level for this scope
    const effectiveLevel = scope ? this.getLogLevel(scope) : this.globalLogLevel;

    // If level is 'off', nothing should be logged
    if (effectiveLevel === 'off') {
      return false;
    }

    // Check if the message's level meets the minimum threshold
    return this.logLevels[level] >= this.logLevels[effectiveLevel];
  }

  /**
   * Determine if a message should be sent to the server
   */
  shouldSendToServer(level) {
    return this.remoteLogging && this.logLevels[level] >= this.logLevels[this.remoteLogLevel];
  }

  /**
   * Send a log to the server
   */
  sendToServer(level, message, meta = {}) {
    if (!this.shouldSendToServer(level)) return;

    try {
      fetch('/api/logs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          level,
          message,
          source: meta.source || window.location.pathname,
          stack: meta.stack || null,
          timestamp: new Date().toISOString()
        })
      }).catch(err => {
        // Silently fail if the log server is unavailable
        console.error('Failed to send log to server:', err);
      });
    } catch (err) {
      // Catch any JSON stringification errors
      console.error('Error sending log to server:', err);
    }
  }

  /**
   * Log a debug message
   */
  debug(message, meta) {
    if (!this.shouldLog('debug', message)) return;

    if (this.consoleOutput) {
      console.debug(message);
    }
    this.sendToServer('debug', message, meta);
  }

  /**
   * Log an info message
   */
  info(message, meta) {
    if (!this.shouldLog('info', message)) return;

    if (this.consoleOutput) {
      console.info('%c[INFO] ' + message, 'color: #4CAF50');
    }
    this.sendToServer('info', message, meta);
  }

  /**
   * Log a warning message
   */
  warn(message, meta) {
    if (!this.shouldLog('warn', message)) return;

    if (this.consoleOutput) {
      console.warn('%c[WARN] ' + message, 'color: #FF9800');
    }
    this.sendToServer('warn', message, meta);
  }

  /**
   * Log an error message
   */
  error(message, meta = {}) {
    if (!this.shouldLog('error', message)) return;

    if (this.consoleOutput) {
      console.error('%c[ERROR] ' + message, 'color: #F44336');
    }

    // Include stack trace if available
    if (meta.error instanceof Error) {
      meta.stack = meta.error.stack;
    }

    this.sendToServer('error', message, meta);
  }

  /**
   * Create a scoped logger
   */
  scope(scopeName) {
    const scopedLogger = {};
    const methods = ['debug', 'info', 'warn', 'error'];

    methods.forEach(method => {
      scopedLogger[method] = (message, meta = {}) => {
        return this[method](`[${scopeName}] ${message}`, {
          ...meta,
          scope: scopeName
        });
      };
    });

    // Add methods to get/set log level for this scope
    scopedLogger.setLogLevel = (level) => {
      this.setLogLevel(scopeName, level);
    };

    scopedLogger.getLogLevel = () => {
      return this.getLogLevel(scopeName);
    };

    return scopedLogger;
  }
}

// Capture uncaught errors
window.addEventListener('error', (event) => {
  if (window.clientLogger) {
    window.clientLogger.error(event.message || 'Uncaught error', {
      error: event.error,
      source: event.filename,
      lineno: event.lineno,
      colno: event.colno
    });
  }
});

// Capture unhandled promise rejections
window.addEventListener('unhandledrejection', (event) => {
  if (window.clientLogger) {
    window.clientLogger.error('Unhandled Promise rejection: ' + 
      (event.reason?.message || 'Unknown reason'), {
      error: event.reason,
      stack: event.reason?.stack
    });
  }
});

// Create global instance
window.clientLogger = new ClientLogger();

// Export logger instance
export default window.clientLogger;

// Somewhere in your route handler that renders the main page
router.get('/', (req, res) => {
  // Get logging configuration from environment
  const loggingConfig = {
    globalLevel: env.getScopeLogLevel('GLOBAL'),
    scopeLevels: {}
  };

  // Add important scope levels
  ['Auth', 'Features', 'Planes', 'UI'].forEach(scope => {
    loggingConfig.scopeLevels[scope] = env.getScopeLogLevel(scope);
  });

  // Render the page with logging configuration
  res.render('index', {
    title: 'Onshape App',
    loggingConfig: JSON.stringify(loggingConfig)
  });
});

// Import the logger at the top
import logger from './utils/logging.js';

// Create a scoped logger for this module
const log = logger.scope('PartStudioSelector');

// Replace console.debug calls with log.debug
// Before: console.debug('[DEBUG] PartStudioSelector constructor called');
// After:
log.debug('PartStudioSelector constructor called');

