    'Features', 
    'Planes', 
    'UI', 
    'Documents', 
    'OnshapeClient',
    'SvgConverter'
  ].forEach(scope => {
    loggingConfig.scopeLevels[scope] = loadEnv.getScopeLogLevel(scope);
